let i,nouAlumne="",ol=document.getElementById("llistaAlumnes"),items=ol.getElementsByTagName("li");for(i=0;i<items.length;i++)console.log(items[i]);function afegir(){nouAlumne=document.querySelector("#nomAlumne").value;let e=document.createElement("li");e.appendChild(document.createTextNode(nouAlumne)),ol.appendChild(e)}function esborrar(){elimAlumne=document.querySelector("#nomAlumne").value;let e=ol.getElementsByTagName("li"),l=!1;for(i=0;i<e.length;i++)elimAlumne==e[i].textContent&&(l=!0,ol.removeChild(e[i]));l||window.alert("No hem trovat cap alumne amb aquest nom")}function random(){let e=document.getElementById("llistaAlumnes"),l=e.getElementsByTagName("li");for(i=0;i<l.length;i++)e.appendChild(e.children[Math.random()*i|0])}